<template>
    <div class="login">
        <form class="form-signin">
            <h1 class="h3 mb-3 font-weight-normal">Please sign in</h1>
            <label for="inputEmail" class="sr-only">Email address</label>
            <input type="email" id="inputEmail" class="form-control" placeholder="Email address" required=""
                   autofocus="" autocomplete="off"
                   v-model="details.username"
                   style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
            <label for="inputPassword" class="sr-only">Password</label>
            <input type="password" id="inputPassword" class="form-control" placeholder="Password" required=""
                   autocomplete="off"
                   v-model="details.password"
                   style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
            <button
                    @click="sendLoginRequest"
                    class="btn btn-lg btn-primary btn-block" type="button">Sign in
            </button>
        </form>
    </div>
</template>

<script>
  import {mapActions} from 'vuex'

  export default {
    name: 'Login',
    data: function () {
      return {
        details: {
          'grant_type': 'password',
          'client_id': process.env.VUE_APP_API_CLIENT_ID,
          'client_secret': process.env.VUE_APP_API_CLIENT_SECRET,
          'username': null,
          'password': null,
          'scope': ''
        }
      }
    },
    methods: {
      ...mapActions('auth', ['loginUser']),
      sendLoginRequest: function () {
        let app = this
        this.loginUser(this.details).then(function () {
          app.$router.push({'name': 'Home'})
        })
      }
    }
  }
</script>

<style scoped>

    .form-signin {
        width: 100%;
        max-width: 330px;
        padding: 15px;
        margin: auto;
    }

    .form-signin .checkbox {
        font-weight: 400;
    }

    .form-signin .form-control {
        position: relative;
        box-sizing: border-box;
        height: auto;
        padding: 10px;
        font-size: 16px;
    }

    .form-signin .form-control:focus {
        z-index: 2;
    }

    .form-signin input[type="email"] {
        margin-bottom: -1px;
        border-bottom-right-radius: 0;
        border-bottom-left-radius: 0;
    }

    .form-signin input[type="password"] {
        margin-bottom: 10px;
        border-top-left-radius: 0;
        border-top-right-radius: 0;
    }

</style>